import React, { useEffect, useState } from "react";
import Sidebar from "../Sidebar";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  deleteProduct,
  getProduct,
} from "../../../store/ActionCreators/ProductActionCreator";
export default function Product() {
  let [data, setData] = useState([]);
  let dispatch = useDispatch();
  let ProductStateData = useSelector((state) => state.ProductStateData);
  console.log(ProductStateData);
  function deleteItem(id) {
    if (window.confirm(`Are you sure! Cofirm to delete that item! : `)) {
      dispatch(deleteProduct({ id: id }));
    }
  }
  function getApiData() {
    dispatch(getProduct());
    if (ProductStateData.length) {
      setData(ProductStateData.slice(1).reverse());
    }
  }
  useEffect(() => {
    getApiData();
    console.log("getApicalled");
    // console.log(ProductStateData);
    //eslint-disable-next-line
  }, [ProductStateData.length]);
  console.log("Product component called");
  return (
    <>
      {" "}
      <div className="container-fluid my-3">
        <div className="row">
          <div className="col-md-3">
            <Sidebar />
          </div>
          <div className="col-md-9">
            <h5 className="bg-primary w-100 p-2 text-light text-center">
              Product
              <Link to={"/admin/product/create"}>
                <li className="fa fa-plus text-light float-end"></li>
              </Link>
            </h5>
            <div className="table-responsive">
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th>S.N</th>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Category</th>
                    <th>Color/Size</th>
                    <th>Price</th>
                    <th>Stock</th>
                    <th>Pic1</th>
                    <th>Pic2</th>
                    <th>Pic3</th>
                    <th>Pic4</th>
                    <th>Update</th>
                    <th>Delete</th>
                  </tr>
                </thead>
                <tbody>
                  {data.map((item, index) => {
                    return (
                      <tr key={index}>
                        <td>{index + 1}</td>
                        <td>{item.id}</td>
                        <td>{item.name}</td>
                        <td>
                          {item.maincategory}/{item.subcategory}/{item.brand}
                        </td>
                        <td>
                          {item.color}/{item.size}
                        </td>
                        <td>
                          <del className="text-danger">
                            &#8377;{item.baseprice}
                          </del>{" "}
                          &#8377;{item.finalprice}{" "}
                          <sup>{item.discount}%Off</sup>
                        </td>
                        <td>{item.stock}</td>
                        <td>
                          <a
                            href={`/products/${item.pic1}`}
                            target="_blank"
                            rel="noreferrer"
                          >
                            <img
                              src={`/products/${item.pic1}`}
                              height="80px"
                              width="80px"
                              className="rounded"
                              alt=""
                            />
                          </a>
                        </td>
                        <td>
                          <a
                            href={`/products/${item.pic2}`}
                            target="_blank"
                            rel="noreferrer"
                          >
                            <img
                              src={`/products/${item.pic2}`}
                              height="80px"
                              width="80px"
                              className="rounded"
                              alt=""
                            />
                          </a>
                        </td>
                        <td>
                          <a
                            href={`/products/${item.pic3}`}
                            target="_blank"
                            rel="noreferrer"
                          >
                            <img
                              src={`/products/${item.pic3}`}
                              height="80px"
                              width="80px"
                              className="rounded"
                              alt=""
                            />
                          </a>
                        </td>
                        <td>
                          <a
                            href={`/products/${item.pic4}`}
                            target="_blank"
                            rel="noreferrer"
                          >
                            <img
                              src={`/products/${item.pic4}`}
                              height="80px"
                              width="80px"
                              className="rounded"
                              alt=""
                            />
                          </a>
                        </td>
                        <td>
                          <Link to={`/admin/product/update/${item.id}`}>
                            <i className="fa fa-edit text-success"></i>
                          </Link>
                        </td>
                        <td>
                          <button
                            className="btn"
                            onClick={() => deleteItem(item.id)}
                          >
                            <i className="fa fa-trash text-danger"></i>
                          </button>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
